mapper.select:
SELECT tbm.meeting_id as meetingId, tbm.meeting_name as meetingName, tbm.deadline as deadline, tbm.start_date as startDate, tbm.end_date as endDate, tbm.category as category, tbm.explanation_title as explanationTitle, tbm.explanation_content as explanationContent, tbm.created_time as createdTime, tbm.max_participant as maxParticipant, (SELECT COUNT(*) FROM tb_user_by_meeting tubm WHERE tbm.meeting_id = tubm.meeting_id) as curParticipant, tbm.meeting_place as meetingPlace, tbu.nickname as clubName, tbm.club_id as clubId FROM tb_meeting as tbm JOIN tb_user as tbu ON tbm.club_id = tbu.user_id
INNER JOIN tb_club_subscription as tcs ON tcs.club_id = tbm.club_id
INNER JOIN tb_user_by_meeting as tubm ON tubm.meeting_id = tbm.meeting_id
WHERE 1=1
and tbm.club_id = #{clubId}
and tbm.start_date >= #{startDate}
and tbm.end_date <= #{endDate}
and tbm.category = #{category}
tcs.user_id = #{userId}
tubm.user_id = #{userId}
limit #{offset}, #{pageScale}
