mapper.update:

/*	S3FileMapper.uploadProfile	*/
update tb_user
set
	profile_url = #{profileUrl}
where
	user_id = #{userId}

mapper.insert:

/*	S3FileMapper.uploadContent	*/
insert into tb_file (
	user_id, origin_file_name, file_url, mime_type, size, meeting_id
) values (
	#{userId}, #{originFileName}, #{fileUrl}, #{mimeType}, #{size}, #{meetingId}
)

mapper.delete:

/*	S3FileMapper.deleteContent	*/
delete from tb_file
where
	user_id = #{clubId} and meeting_id = #{meetingId}
