mapper.update:

/*	S3FileMapper.uploadProfile	*/
update tb_user
set
	profile_url = #{profileUrl}
where
	user_id = #{userId}

mapper.insert:

/*	S3FileMapper.uploadContent	*/
INSERT INTO tb_file (
	user_id, origin_file_name, file_url, mime_type, size
) SELECT (
	#{userId}, #{originFileName}, #{fileUrl}, #{mimeType}, #{size}
) FROM DUAL
WHERE NOT EXISTS
(SELECT *
FROM tb_file
WHERE file_url = #{fileUrl}
)


/*	S3FileMapper.createRelationFileMeeting	*/
insert into tb_rmf (
	file_id, meeting_id, index
) select
	( SELECT file_id
	  FROM tb_file
	  WHERE file_url = #{fileUrl}),
	  #{meetingId},
	  #{index}
from dual
where exists(select * from tb_rmf where file_id = #{fileId} and meeting_id = #{meetingId}
