mapper.insert:

/*	UserMapper.insertUser	*/
insert into tb_user (
	password, email, nickname, created_time, role
) values (
	#{password}, #{email}, #{nickname}, #{createdTime}, #{role}
)

mapper.select:

/*	UserMapper.selectUserEmail	*/
select count(email)
from tb_user
where email = #{email}


select email
from tb_user
where user_id = #{userId}


/*	UserMapper.selectUserNicknameIsOk	*/
select count(nickname)
from tb_user
where nickname = #{nickname}


/*	UserMapper.selectUserNickname	*/
select nickname
from tb_user
where user_id = #{userId}


/*	UserMapper.selectCode	*/
select code as code
from tb_codeemail
where email = #{email}


/*	UserMapper.selectCodeInfo	*/
select email as email, created_time as createdTime
from tb_codeemail
where code = #{code}

mapper.insert:

/*	UserMapper.insertCodeEmail	*/
insert into tb_codeemail (
	email, code, created_time
) values (
	#{email}, #{code}, #{time}
) on duplicate key update email=#{email}, code=#{code}, created_time=#{time}

mapper.update:

UPDATE tb_user
SET device_token = #{deviceToken}
WHERE email = #{email}


UPDATE tb_codeemail
SET code=#{code},
    created_time=#{time}
WHERE email=#{email}


UPDATE tb_user
SET nickname=#{nickname}
WHERE email=#{email}


UPDATE tb_user
SET password=#{password}
WHERE email=#{email}

mapper.select:

SELECT device_token
FROM tb_user
WHERE user_id = #{userId}
